import React, { useState } from 'react';

const Gallery = () => {
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedVideo, setSelectedVideo] = useState(null);

  const exercises = [
    {
      id: 1,
      title: "Push-ups",
      category: "chest",
      difficulty: "beginner",
      equipment: "none",
      image: "/images/pushup.png",
      videoUrl: "https://www.youtube.com/embed/IODxDxX7oi4",
      description: "Classic upper body exercise targeting chest, shoulders, and triceps."
    },
    {
      id: 7,
      title: "Bench Press",
      category: "chest",
      difficulty: "intermediate",
      equipment: "barbell",
      image: "/images/bench%20Press.png",
      videoUrl: "https://www.youtube.com/embed/rT7DgCr-3pg",
      description: "Primary chest exercise with barbell or dumbbells."
    },
    {
      id: 8,
      title: "Shoulder Press",
      category: "shoulders",
      difficulty: "intermediate",
      equipment: "dumbbells",
      image: "/images/shoulder%20Press.png",
      videoUrl: "https://www.youtube.com/embed/vlFGTI5JzjI",
      description: "Overhead press for shoulder development."
    },
    {
      id: 9,
      title: "Bicep Curls",
      category: "arms",
      difficulty: "beginner",
      equipment: "dumbbells",
      image: "/images/bicep%20Curls.png",
      videoUrl: "https://www.youtube.com/embed/ykJmrh5PWE0",
      description: "Isolation exercise for bicep development."
    },
    {
      id: 2,
      title: "Squats",
      category: "legs",
      difficulty: "beginner",
      equipment: "none",
      image: "/images/squats.png",
      videoUrl: "https://www.youtube.com/embed/aclHkVaku9I",
      description: "Fundamental lower body exercise for quads, glutes, and hamstrings."
    },
    {
      id: 3,
      title: "Pull-ups",
      category: "back",
      difficulty: "intermediate",
      equipment: "bar",
      image: "/images/pullup.png",
      videoUrl: "https://www.youtube.com/embed/eGo4IYlbE5g",
      description: "Advanced upper body exercise for back and biceps development."
    },
    {
      id: 4,
      title: "Deadlifts",
      category: "full-body",
      difficulty: "advanced",
      equipment: "barbell",
      image: "/images/deadlifts.png",
      videoUrl: "https://www.youtube.com/embed/r9MlZDsNXP0",
      description: "Compound exercise targeting multiple muscle groups."
    },
    {
      id: 5,
      title: "Plank",
      category: "core",
      difficulty: "beginner",
      equipment: "none",
      image: "/images/plank.png",
      videoUrl: "https://www.youtube.com/embed/ASdvN_XEl_c",
      description: "Core strengthening exercise for abs and stability."
    },
    {
      id: 6,
      title: "Lunges",
      category: "legs",
      difficulty: "beginner",
      equipment: "none",
      image: "/images/lunges.png",
      videoUrl: "https://www.youtube.com/embed/wrwwXE_x-pQ",
      description: "Unilateral leg exercise for balance and strength."
    },
    
  ];

  const categories = ['all', 'chest', 'back', 'legs', 'shoulders', 'arms', 'core', 'full-body'];

  const filteredExercises = exercises.filter(exercise => {
    const matchesCategory = selectedCategory === 'all' || exercise.category === selectedCategory;
    const matchesSearch = exercise.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         exercise.description.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const getDifficultyColor = (difficulty) => {
    switch(difficulty) {
      case 'beginner': return 'text-success';
      case 'intermediate': return 'text-warning';
      case 'advanced': return 'text-danger';
      default: return 'text-secondary';
    }
  };

  const openVideoModal = (exercise) => {
    setSelectedVideo(exercise);
  };

  const closeVideoModal = () => {
    setSelectedVideo(null);
  };

  return (
    <>
      {/* Hero Section */}
      <section className="hero-section text-white text-center py-5 position-relative overflow-hidden" 
             style={{ 
               backgroundImage: 'url(/images/group.png)',
               backgroundSize: 'cover',
               backgroundPosition: 'center',
               backgroundAttachment: 'fixed',
               minHeight: '70vh',
               position: 'relative'
             }}>
        <div className="hero-overlay" style={{
          background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.85) 0%, rgba(118, 75, 162, 0.85) 100%)',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          transition: 'all 0.5s ease'
        }}></div>
        
        {/* Animated particles effect */}
        <div className="hero-particles" style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          overflow: 'hidden'
        }}>
          {[...Array(20)].map((_, i) => (
            <div
              key={i}
              className="particle"
              style={{
                position: 'absolute',
                width: Math.random() * 4 + 2 + 'px',
                height: Math.random() * 4 + 2 + 'px',
                background: 'rgba(255, 255, 255, 0.8)',
                borderRadius: '50%',
                left: Math.random() * 100 + '%',
                top: Math.random() * 100 + '%',
                animation: `float ${Math.random() * 10 + 10}s linear infinite`,
                animationDelay: Math.random() * 5 + 's'
              }}
            />
          ))}
        </div>
        
        <div className="container position-relative" style={{ zIndex: 2 }}>
          <div className="hero-content">
            <div className="animate-fade-in">
              <h1 className="display-3 fw-bold mb-4 text-shadow" style={{
                textShadow: '2px 2px 4px rgba(0,0,0,0.5)',
                animation: 'slideInDown 1s ease-out'
              }}>
                Exercise Gallery
              </h1>
              <p className="lead mb-5 animate-slide-up" style={{
                fontSize: '1.3rem',
                textShadow: '1px 1px 2px rgba(0,0,0,0.5)',
                animation: 'slideInUp 1s ease-out 0.3s both'
              }}>
                Explore our comprehensive collection of exercises for all fitness levels
              </p>
              
              {/* Hero stats */}
              <div className="row justify-content-center mt-5">
                <div className="col-md-3 col-6 mb-3">
                  <div className="hero-stat-card">
                    <i className="fas fa-dumbbell fa-2x mb-3" style={{ animation: 'bounce 2s infinite' }}></i>
                    <h3 className="fw-bold">50+</h3>
                    <p className="mb-0">Exercises</p>
                  </div>
                </div>
                <div className="col-md-3 col-6 mb-3">
                  <div className="hero-stat-card">
                    <i className="fas fa-users fa-2x mb-3" style={{ animation: 'bounce 2s infinite 0.5s' }}></i>
                    <h3 className="fw-bold">All</h3>
                    <p className="mb-0">Levels</p>
                  </div>
                </div>
                <div className="col-md-3 col-6 mb-3">
                  <div className="hero-stat-card">
                    <i className="fas fa-play-circle fa-2x mb-3" style={{ animation: 'bounce 2s infinite 1s' }}></i>
                    <h3 className="fw-bold">HD</h3>
                    <p className="mb-0">Videos</p>
                  </div>
                </div>
                <div className="col-md-3 col-6 mb-3">
                  <div className="hero-stat-card">
                    <i className="fas fa-chart-line fa-2x mb-3" style={{ animation: 'bounce 2s infinite 1.5s' }}></i>
                    <h3 className="fw-bold">Track</h3>
                    <p className="mb-0">Progress</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Gallery Section */}
      <section className="py-5">
        <div className="container">
          {/* Filters */}
          <div className="row mb-4">
            <div className="col-md-6">
              <div className="input-group">
                <span className="input-group-text">
                  <i className="fas fa-search"></i>
                </span>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Search exercises..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </div>
            <div className="col-md-6">
              <select
                className="form-select"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                {categories.map(category => (
                  <option key={category} value={category}>
                    {category.charAt(0).toUpperCase() + category.slice(1).replace('-', ' ')}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Exercise Grid */}
          <div className="row">
            {filteredExercises.map(exercise => (
              <div key={exercise.id} className="col-lg-4 col-md-6 mb-4">
                <div className="card h-100 shadow-sm exercise-card">
                  <img 
                    src={exercise.image} 
                    className="card-img-top" 
                    alt={exercise.title}
                    style={{ height: '200px', objectFit: 'cover' }}
                  />
                  <div className="card-body d-flex flex-column">
                    <h5 className="card-title">{exercise.title}</h5>
                    <p className="card-text">{exercise.description}</p>
                    <div className="d-flex justify-content-between align-items-center mb-3">
                      <span className={`badge bg-secondary me-2`}>
                        <i className="fas fa-tag me-1"></i>
                        {exercise.category}
                      </span>
                      <span className={`badge ${getDifficultyColor(exercise.difficulty)}`}>
                        <i className="fas fa-signal me-1"></i>
                        {exercise.difficulty}
                      </span>
                    </div>
                    <div className="mb-2">
                      <small className="text-muted">
                        <i className="fas fa-tools me-1"></i>
                        {exercise.equipment === 'none' ? 'No Equipment' : exercise.equipment}
                      </small>
                    </div>
                    <div className="d-grid gap-2 mt-auto">
                      <button 
                        className="btn btn-primary btn-sm"
                        onClick={() => openVideoModal(exercise)}
                      >
                        <i className="fas fa-play-circle me-2"></i>
                        Watch Demo Video
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {filteredExercises.length === 0 && (
            <div className="text-center py-5">
              <i className="fas fa-search fa-3x text-muted mb-3"></i>
              <h4>No exercises found</h4>
              <p className="text-muted">
                Try adjusting your search or category filter
              </p>
            </div>
          )}
        </div>
      </section>

      {/* Video Modal */}
      {selectedVideo && (
        <div 
          className="modal fade show d-block" 
          style={{ backgroundColor: 'rgba(0,0,0,0.8)' }}
          onClick={closeVideoModal}
        >
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">{selectedVideo.title} - Demo Video</h5>
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={closeVideoModal}
                ></button>
              </div>
              <div className="modal-body text-center">
                <div className="ratio ratio-16x9">
                  <iframe
                    src={selectedVideo.videoUrl}
                    title={selectedVideo.title}
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                    allowFullScreen
                    className="w-100 h-100"
                    style={{ border: 'none' }}
                  ></iframe>
                </div>
                <p className="mt-3 text-muted">{selectedVideo.description}</p>
              </div>
            </div>
          </div>
        </div>
      )}

      <style>{`
        .exercise-card {
          transition: transform 0.3s ease, box-shadow 0.3s ease;
        }
        .exercise-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
        }
        .modal {
          z-index: 9999;
        }
        .modal-dialog {
          max-width: 90vw;
        }
        
        /* Hero animations */
        .hero-stat-card {
          background: rgba(255, 255, 255, 0.15);
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255, 255, 255, 0.25);
          border-radius: 15px;
          padding: 20px;
          text-align: center;
          transition: all 0.4s ease;
          margin: 10px;
        }
        
        .hero-stat-card:hover {
          transform: translateY(-10px) scale(1.05);
          box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
          background: rgba(255, 255, 255, 0.25);
        }
        
        .hero-stat-card h3 {
          font-size: 2.5rem;
          font-weight: bold;
          margin: 10px 0 5px 0;
          color: #fff;
          text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
        }
        
        .hero-stat-card p {
          margin: 0;
          font-size: 0.9rem;
          color: rgba(255, 255, 255, 0.9);
          text-transform: uppercase;
          letter-spacing: 1px;
        }
        
        @keyframes float {
          0%, 100% {
            transform: translateY(0px) translateX(0px);
            opacity: 0;
          }
          10% {
            opacity: 1;
          }
          90% {
            opacity: 1;
          }
          100% {
            transform: translateY(-100vh) translateX(100px);
            opacity: 0;
          }
        }
        
        @keyframes slideInDown {
          from {
            opacity: 0;
            transform: translateY(-50px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        @keyframes slideInUp {
          from {
            opacity: 0;
            transform: translateY(50px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        @keyframes bounce {
          0%, 20%, 50%, 80%, 100% {
            transform: translateY(0);
          }
          40% {
            transform: translateY(-10px);
          }
          60% {
            transform: translateY(-5px);
          }
        }
        
        .text-shadow {
          text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
        }
      `}</style>
    </>
  );
};

export default Gallery;
